    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // TODO add your handling code here:
        QuineMcCluskeyAlgorithm minimizer = new QuineMcCluskeyAlgorithm();                
        String result = "";        
        
        try {
            Expression exp = new Expression(jTextInput.getText().toString());
            minimizer.optimize(exp);
            result = exp.getExpression();
        } catch (Exception ex) {
            result = "error occur !";
        }
        jTextAreaOutput.setText(result);
    }//GEN-LAST:event_jButton1ActionPerformed

